package com.springboot.blog;import com.springboot.blog.entity.Role;import com.springboot.blog.repository.RoleRepository;import io.swagger.v3.oas.annotations.OpenAPIDefinition;import io.swagger.v3.oas.annotations.info.Info;import org.modelmapper.ModelMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.CommandLineRunner;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.context.annotation.Bean;@SpringBootApplication@OpenAPIDefinition(		info = @Info(				title = "Spring boot Blog App Rest API's",				description="Spring boot Blog App Rest API's documentation",				version = "v1.0"		))public class SpringbootBlogRestApiApplication implements CommandLineRunner {	@Bean	public ModelMapper modelMapper() {		return new ModelMapper();	}	public static void main(String[] args) {		SpringApplication.run(SpringbootBlogRestApiApplication.class, args);	}	@Autowired	RoleRepository roleRepository;	@Override	public void run(String... args) throws Exception {		com.springboot.blog.entity.Role adminRole = new Role();		adminRole.setName("ROLE_ADMIN");		roleRepository.save(adminRole);		com.springboot.blog.entity.Role userRole = new Role();		userRole.setName("ROLE_USER");		roleRepository.save(userRole);	}}